mapper.select:
SELECT 
FROM t_invoice
WHERE task_id = #{taskId} and invoice_number = #{invoiceNumber}

SELECT 
FROM t_invoice
where (received_amount is null OR received_amount = 0 OR received_amount < invoice_amount)

    and dep_id = #{depId}


    and credit_limit = #{creditLimit}


    and invoice_office LIKE concat('%',#{invoice_office},'%')


    and contract_user LIKE concat('%',#{contractUser},'%')


    and invoice_date >= #{startDate}


    and invoice_date <= #{endDate}

SELECT 
FROM t_invoice
WHERE  invoice_date >= #{startDate} and invoice_date <= #{endDate}

SELECT

FROM t_invoice


        and invoice_type = #{invoiceType}


        and contract_user = #{contractUser}


        and department_name LIKE concat('%',#{departmentName},'%')


        and invoice_date >= #{startDate}


        and invoice_date <= #{endDate}

SELECT

FROM t_invoice


        and contract_user = #{contractUser}


        and department_name LIKE concat('%',#{departmentName},'%')


        and credit_limit = #{creditLimit}


        and invoice_office LIKE concat('%',#{invoiceOffice},'%')


        and invoice_date >= #{startDate}


        and invoice_date <= #{endDate}
